import React, { useState } from "react";
import { ActionBtn } from "./buttons";

function UserLink (props) {
    const {user, includeFullName} = props
    
    return <React.Fragment>
        {user.first_name} {user.last_name} @{user.username}
    </React.Fragment>
}

function UserPicture (props) {
    const {user} = props;
    return <span className="mx-1 px-3 py-2 rounded-circle bg-dark text-white">
        {user.username[0]}
    </span>
}

export function ParentTweet(props) {
    const { tweet } = props;
    return (
        <>
            {tweet.parent ? 
                <Tweet isRetweet retweeter={props.retweeter} hideActions className={" "} tweet={tweet.parent} /> : null}
        </>
    );
}

export function Tweet(props) {
    const { tweet, didRetweet, hideActions, isRetweet, retweeter } = props;
    const [actionTweet, setActionTweet] = useState(
        props.tweet ? props.tweet : null
    );
    let className = props.className
        ? props.className
        : "col-10 mx-auto col-md-6";
    className = isRetweet === true ? `${className} p-2 border rounded` : className
    const path = window.location.pathname;
    const match = path.match(/(?<tweetid>\d+)/);
    const urlTweetId = match ? match.groups.tweetid : -1;

    const isDetail = `${tweet.id}` === `${urlTweetId}`;
    const handleLink = (event) => {
        event.preventDefault();
        window.location.href = `/${tweet.id}`;
    };
    const handlePerformAction = (newActionTweet, status) => {
        if (status === 200) {
            setActionTweet(newActionTweet);
        } else if (status === 201) {
            // let the tweet list know.
            if (didRetweet) {
                didRetweet(newActionTweet);
            }
        }
    };
    return (
        <>
            <div className={className}>
                {isRetweet === true && 
                    <div className='mb-2'>
                        <span className='small text-muted'>Retweet via @{retweeter.username}</span>
                    </div>}
                <div className="d-flex">
                    <div className="col-1">
                        <UserPicture user={tweet.user} />
                    </div>
                    <div className="col-11">
                        <div>
                            <p>
                                <UserLink user={tweet}/>
                            </p>
                            <p>{tweet.content}</p>
                            <ParentTweet tweet={tweet} retweeter={tweet.user} />
                        </div>
                        {/* if actionTweet and hideActions are not = to true then hide the buttons */}
                        <div className="btn btn-group px-0">
                            {actionTweet && hideActions !== true && (
                                <React.Fragment>
                                    <ActionBtn
                                        tweet={actionTweet}
                                        didPerformAction={handlePerformAction}
                                        action={{
                                            type: "like",
                                            display: "Likes",
                                        }}
                                    />
                                    <ActionBtn
                                        tweet={actionTweet}
                                        didPerformAction={handlePerformAction}
                                        action={{
                                            type: "unlike",
                                            display: "Unlike",
                                        }}
                                    />
                                    <ActionBtn
                                        tweet={actionTweet}
                                        didPerformAction={handlePerformAction}
                                        action={{
                                            type: "retweet",
                                            display: "Retweet",
                                        }}
                                    />
                                </React.Fragment>
                            )}
                            {isDetail === true ? null : (
                                <button
                                    className="btn btn-outline-primary btn-sm"
                                    onClick={handleLink}
                                >
                                    View
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
